INSERT INTO `Event` (`EventID`, `Name`, `Year`) VALUES
(1, 'Het dubbele huis heeft aan de achterzijde twee trapgevels in traditionele architectuur, waarvan de rechtergevel in 1954 gereconstrueerd is.', 1954),
(2, 'In 1634 verkocht hij het huis aan de wapenhandelaar Louis de Geer (1587–1652).', 1634),
(3, 'Diens zoon verkocht het huis in 1779.', 1779),
(4, 'In 1811 vestigde kunsthandel ''''De Roos'''' zich in het pand.', 1811),
(5, 'Na een restauratie in 1907 werd het gebouw van 1909 tot 1931 gebruikt door het Conservatorium van Amsterdam.', 1907),
(6, 'Ter gelegenheid hiervan werd op 17 september een replica teruggehangen van het tijdens de Beeldenstorm van 1566 verdwenen Angelusklokje in het kleine torentje boven op het kerkdak.', 1566),
(7, 'Daarna volgden twee kapellen die de kerk het uiterlijk van een kruiskerk gaven: de Sint Joriskapel aan de noordzijde, gebouwd in 1380-1412 (tijdens de restauratie is het jaar 1412 in een balk van het gewelf gevonden), en de Sint Sebastiaanskapel aan de zuidzijde, gebouwd in 1450-1460.', 1412),
(8, 'In 1994/1998 is de kerk opnieuw gerestaureerd.', 1994),
(9, 'Vanaf 1632 vonden de vergaderingen van de Kerkenraad afwisselend plaats in de Oude en Nieuwe Kerk.', 1632),
(10, 'Tijdens een verbouwing in 1972 werden ten behoeve van een betere lichttoetreding de glas-in-loodramen ingekort, de originele wandbespanning in een lichtere tint vernieuwd en de oorspronkelijke wandlampen, plafonnières en lichtkroon verwijderd.', 1972),
(11, 'De originele, in opengewerkt messing uitgevoerde, lichtkroon werd in 1974 herplaatst.', 1974),
(12, 'In 2004 is het GVB naar elders vertrokken.', 2004),
(13, 'Op 8 juni 2007 werd het gebouw na een ingrijpende verbouwing een 5 sterren deluxe hotel/restaurant/zalencentrum met 165 hotelkamers, genaamd Grand Hotel Amrâth Amsterdam.', 2007),
(14, 'Op 19 november 1798 werd, op initiatief van de minister van financiën, Alexander Gogel, besloten de resterende verzameling, bestaande uit Italiaanse kunst, portretten van de Oranjes en vaderlandse rariteiten, onder te brengen in een nationaal museum naar Frans voorbeeld.', 1798),
(15, 'In 1808 verhuisde het museum in opdracht van Lodewijk Napoleon naar Amsterdam, waar het vanaf 1809 samen met de kunstverzameling van Amsterdam — waaronder de Nachtwacht — werd ondergebracht op de bovenverdieping van het Paleis op de Dam.', 1808),
(16, 'In 1888 is het toen als museum ingericht.', 1888),
(17, 'De kerk werd in de jaren na 1243 gebouwd op basis van een vroeger kerkgebouw.', 1243),
(18, 'De oorsprong van de kerk ligt bij de monniken van de Sint-Baafsabdij uit Gent, die op deze locatie in 959 een kleine kerk bouwden.', 959),
(19, 'Het paleis is na restauratie in de jaren ''70 sinds 1984 ingericht als museum.', 1984),
(20, 'Sinds 1971 is het eigendom van de Staat der Nederlanden.', 1971),
(21, 'Willem IV overleed in 1751 en zijn vrouw en zoon bleven in de zomer op Soestdijk wonen.', 1751),
